import React from 'react';

const Form = ({
  title, action, error, children,
}) => {
  return (
    <>
      <form action={action}>
        <h2>{title}</h2>
  { error ? <div className="Error"><span>{erro}</span></div> : '' }
        {children}
      </form>
      <style jsx>
        {`
          form {
            display: flex;
            flex-direction: column;
            width: 264px;
          }
          h2 {
            margin-bottom: 5px;
            color: #64D7EB;
            font-size: 28px;
          }
          .Error {
            min-width: 100%;
            max-width: 100%;
            min-height: 30px;
            background: #ff0000;
            margin-bottom: 5px;
            display: flex;
            align-items: center;
            color: white;
            border-radius: 7px;
          }
          .Error span {
            margin: 7px;
          }
        `}
      </style>
    </>
  );
};

export default Form;
