import React from 'react';

const ToDoCard = ({ title, description, done }) => {
  return (
    <>
      <div className="card">
        <div className="card-header">
          <h3>
            {title}
          </h3>
          <div className="done-button">
            <button type="button" className="trash"><img src="/images/trash-icon.svg" alt="delete to do" /></button>
            <span>
              done:
            </span>
            <button type="button" className={done ? 'check done' : 'check'} />
          </div>
        </div>
        <p>
          {description}
        </p>
      </div>
      <style jsx>
        {`
          .card {
            border: 1px solid #0000001A;
            border-radius: 7px;
            box-shadow: 3px 5px 10px #0000004D;
            padding: 10px;
            color: #0000008C;
          }
          .card-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 7px;
          }
          .done-button {
            display: flex;
            align-items: center;
          }
          .done-button span {
            margin-right: 5px;
          }
          .check {
            width: 20px;
            height: 20px;
            border-radius: 50%;
            border: none;
            background: #ff0000b8;
            outline: none;
          }
          .check.done {
            background: #0cc30ccc;
          }
          .trash {
            width: 20px;
          }
          .trash img {
            width
          }
        `}
      </style>
    </>
  );
};

export default ToDoCard;
