import { useState } from 'react';
import cookie from 'js-cookie';
import Router from 'next/router';

const login = () => {
  const [username, setUsername] = useState('');
  const [usernameError, setUsernameError] = useState('');
  const [password, setPassword] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [loginError, setLoginError] = useState('');

  const handleSubmit = async (e) => {
    setLoginError('');
    e.preventDefault();
    try {
      const request = await fetch('https://todo-nrestrepo05-api.herokuapp.com/api-token-auth/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username,
          password,
        }),
      });
      const data = await request.json();
      if (data && data.password) return setPasswordError(data.password);
      if (data && data.username) return setUsernameError(data.username);
      if (request.status === 400 && data.non_field_errors) {
        setLoginError(data.non_field_errors);
      }
      if (data && data.token) {
        cookie.set('token', data.token);
        Router.push('/');
      }
    } catch (error) {
      setLoginError(error);
    }
  };

  return (
    <>
      <form action="" method="post">
        { loginError ? <div className="Error">Usuario o contrase√±a incorrectos</div> : '' }
        <input
          type="text"
          name="username"
          id="username"
          value={username}
          placeholder="username"
          onChange={(e) => setUsername(e.target.value)}
        />
        { usernameError ? <span>{usernameError}</span> : ''}
        <input
          type="password"
          name="password"
          id="password"
          value={password}
          placeholder="password"
          onChange={(e) => setPassword(e.target.value)}
        />
        { passwordError ? <span>{passwordError}</span> : ''}
        <button type="submit" onClick={handleSubmit}>
          Log In!
        </button>
      </form>
    </>
  );
};

export default login;
