import { useContext, useState } from 'react';
import Link from 'next/link';
import Router from 'next/router';
import { userContext } from '../userContext';
import useInputValue from '../hooks/useInputValue';
import Layout from '../components/Layout';
import Form from '../components/Form';
import Input from '../components/Input';
import Button from '../components/Button';

const signup = () => {
  const [signUpError, setSignUpError] = useState('');
  const [passwordConfirmationError, setPasswordConfirmationError] = useState('');
  const { activateAuth } = useContext(userContext);
  const username = useInputValue('');
  const password = useInputValue('');
  const firstName = useInputValue('');
  const lastName = useInputValue('');
  const email = useInputValue('');
  const passwordConfirmation = useInputValue('');

  const validatePasswordConfirmation = () => {
    const errors = [];
    const fields = {};
    if (username) {
      fields.username = username;
    } else {
      errors.push = 'The username is required';
    }
    if (password === passwordConfirmation) {
      fields.password = password.value;
    } else {
      errors.push = 'The password and password confirmation must be equal';
    }
    if (firstName) {
      fields.firstName = firstName;
    } else {
      errors.push = 'First name is required';
    }
    if (lastName) {
      fields.lastName = lastName;
    } else {
      errors.push = 'Last name is required';
    }
    if (email) {
      fields.email = email;
    } else {
      errors.push = 'Email is required';
    }
    // setPasswordConfirmationError(true);
    throw new Error('The password and the password confirmation must be equals');
  };

  const handleSignUpSubmit = async (e) => {
    setSignUpError('');
    e.preventDefault();
    try {
      const request = await fetch('https://todo-nrestrepo05-api.herokuapp.com/api/v1/users/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: username.value,
          password: validatePasswordConfirmation(),
        }),
      });
      const data = await request.json();
      if (request.status === 400) {
        // setSignUpError(true);
      }
      if (data && data.token) {
        activateAuth(data.token);
        Router.push('/');
      }
    } catch (error) {
      console.log(error);
      console.log(error.message);
      setSignUpError(error);
      setPasswordConfirmationError(error.message);
    }
  };

  return (
    <>
      <Layout>
        <div>
          <Form title="Log In!" error={passwordConfirmationError} errorMessage="The password and the password confirmation must be equals">
            <Input
              type="text"
              name="username"
              placeholder="username"
              {...username}
            />
            <Input
              type="password"
              name="password"
              placeholder="password"
              {...password}
            />
            <Input
              type="password"
              name="password_confirmation"
              placeholder="password confirmation"
              {...passwordConfirmation}
            />
            <Button type="submit" onClick={handleSignUpSubmit}>
              Log In!
            </Button>
            <span>Do you have an account yet?&nbsp;
              <Link href="/signup">
                <a>Log in here!</a>
              </Link>
            </span>
          </Form>
        </div>
      </Layout>
      <style jsx global>
        {`
          .page_container {
            display: flex;
            align-items: center;
            justify-content: center;
          }
        `}
      </style>
    </>
  );
};

export default signup;
