import { useState, useContext } from 'react';
import Router from 'next/router';
import { userContext } from '../userContext';
import Layout from '../components/Layout';
import useInputValue from '../hooks/useInputValue';

const login = () => {
  // const [username, setUsername] = useState('');
  // const [password, setPassword] = useState('');
  const [loginError, setLoginError] = useState('');
  const { activateAuth } = useContext(userContext);
  const username = useInputValue('');
  const password = useInputValue('');

  const handleSubmit = async (e) => {
    setLoginError('');
    e.preventDefault();
    try {
      const request = await fetch('https://todo-nrestrepo05-api.herokuapp.com/api-token-auth/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: username.value,
          password,
        }),
      });
      const data = await request.json();
      if (request.status === 400) {
        setLoginError(true);
      }
      if (data && data.token) {
        activateAuth(data.token);
        Router.push('/');
      }
    } catch (error) {
      setLoginError(error);
    }
  };

  return (
    <Layout>
      <form action="" method="post">
        { loginError ? <div className="Error">Usuario o contrase√±a incorrectos</div> : '' }
        <input
          type="text"
          name="username"
          placeholder="username"
          {...username}
        />
        <input
          type="password"
          name="password"
          id="password"
          value={password}
          placeholder="password"
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit" onClick={handleSubmit}>
          Log In!
        </button>
      </form>
    </Layout>
  );
};

export default login;
