import Link from 'next/link';
import { useState } from 'react';

const Header = () => {
  const [isActive, setIsAtive] = useState(false);

  const handleMenuClick = () => {
    if (isActive) return setIsAtive(false);
    return setIsAtive(true);
  };

  return (
    <>
      <header>
        <div className="title-icon">
          <h1>
            ToDo App
          </h1>
          <span onClick={handleMenuClick} role="button" tabIndex={0}>
            <img src="/images/menu-icon.svg" alt="menu-icon" />
          </span>

        </div>
        <nav className="menu">
          <ul className={isActive ? 'is-active' : ''}>
            <li>
              <Link href="/to-do/">
                <a>To Do</a>
              </Link>
            </li>
            <li><a href="/">Done</a></li>
            <li><a href="/">Account</a></li>
          </ul>
        </nav>
      </header>
      <style jsx>
        {`
        header {
          max-width: 100%;
          height: 60px;
          background: #64D7EB;
          color: white;
          display: flex;
          justify-content: space-between;
          padding: 0 50px;
          align-items: center;
          box-shadow: 7px 0px 10px rgba(0, 0, 0, 0.1);
        }
        span:focus {
          outline: none;
        }

        h1 {
          font-size: 36px;
        }
        ul {
          display: flex;
          flex-direction: column;
        }
        ul.is-active {
          display: flex;
          color: red;
        }
        li {
          margin-right: 15px;
          list-style: none;
        }
        li a {
          text-decoration: none;
          color: white;
          font-weight: 600;
          font-size: 20px;
        }
        @media screen and (min-width: 460px){
          header {

          }
          img {
            display: none
          }
          ul {
            display: flex;
          }
        } 

      `}
      </style>
    </>
  );
};

export default Header;
