import Link from 'next/link';
import { useState } from 'react';

const Header = () => {
  const logged = false;
  const [isActive, setIsAtive] = useState(false);

  const handleMenuClick = () => {
    if (isActive) return setIsAtive(false);
    return setIsAtive(true);
  };

  return (
    <>
      <header>
        <div className="title-icon">
          <h1>
            <Link href="/">
              <a>ToDo App</a>
            </Link>
          </h1>
          <span onClick={handleMenuClick} role="button" tabIndex={0}>
            <img src="/images/menu-icon.svg" alt="menu-icon" />
          </span>
        </div>
        <nav className="menu">
          <ul className={isActive ? 'is-active' : ''}>
            {logged
              ? (
                <>
                  <li>
                    <Link href="/to-do/">
                      <a onClick={() => setIsAtive(false)}>To Do</a>
                    </Link>
                  </li>
                  <li>
                    <Link href="/">
                      <a onClick={() => setIsAtive(false)}>Done</a>
                    </Link>
                  </li>
                  <Link href="/">
                    <a onClick={() => setIsAtive(false)}>Account</a>
                  </Link>
                </>
              )
              : (
                <>
                  <li>
                    <Link href="/" className="login-button">
                      <a onClick={() => setIsAtive(false)}>Login</a>
                    </Link>
                  </li>
                  <li>
                    <Link href="/" className="signup-button">
                      <a onClick={() => setIsAtive(false)}>Register</a>
                    </Link>
                  </li>
                </>
              )}
          </ul>
        </nav>
      </header>
      <style jsx>
        {`
        header {
          max-width: 100%;
          background: #64D7EB;
          color: white;
          display: flex;
          box-shadow: 7px 0px 10px rgba(0, 0, 0, 0.1);
          flex-direction: column;
          padding: 0 50px;
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          z-index: 99999;
        }
        .title-icon {
          align-items: center;
          height: 60px;
          display: flex;
          width: 100%;
          justify-content: space-between;
        }
        span:focus {
          outline: none;
        }

        h1 {
          font-size: 36px;
        }
        h1 a {
          text-decoration: none;
          color: white;
        }
        ul {
          display: none;
        }
        ul.is-active {
          display: flex;
          flex-direction: column;
          height: calc(100vh - 60px);
          justify-content: center;
          align-items: center;
          position: fixed;
          background: #64D7EB;
          top: 60px;
          left: 0;
          right: 0;
        }
        li {
          margin: 10px 0;
          list-style: none;
        }
        li a {
          text-decoration: none;
          color: white;
          font-weight: 600;
          font-size: 20px;
        }
        @media screen and (min-width: 460px){
          header {
            flex-direction: initial;
            align-items: center;
          }
          h1 {
            font-size: 32px;
          }
          img {
            display: none
          }
          ul {
            display: flex;
            flex-direction: initial;
          }
          li {
            margin-right: 15px;
            white-space:nowrap;
          }
          li a {
            font-size: 16px;
          }
        } 
        @media screen and (min-width: 560px){
          h1 {
            font-size: 36px;
          }
          li a {
            font-size: 20px;
          }
        }
      `}
      </style>
    </>
  );
};

export default Header;
