import React from 'react';

const Form = ({ title, action, children }) => {
  return (
    <>
      <form action={action}>
        <h2>{title}</h2>
        {children}
      </form>
      <style jsx>
        {`
          form {
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
          }
          h2 {
            text-align: right
          }
        `}
      </style>
    </>
  );
};

export default Form;
