import { useContext, useState } from 'react';
import Link from 'next/link';
import Router from 'next/router';
import { userContext } from '../userContext';
import useInputValue from '../hooks/useInputValue';
import Layout from '../components/Layout';
import Form from '../components/Form';
import Input from '../components/Input';
import Button from '../components/Button';

const signup = () => {
  const [signUpError, setSignUpError] = useState('');
  const [passwordConfirmationError, setPasswordConfirmationError] = useState('');
  const { activateAuth } = useContext(userContext);
  const username = useInputValue('');
  const password = useInputValue('');
  const passwordConfirmation = useInputValue('');

  const handleSignUpSubmit = async (e) => {
    setSignUpError('');
    e.preventDefault();
    try {
      const request = await fetch('https://todo-nrestrepo05-api.herokuapp.com/api/v1/users/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: username.value,
          password: (() => {
            if (password === passwordConfirmation) {
              return password.value;
            }
            const passwordsDoesNotMatch = 'The password and the password confirmation must be equals';
            setSignUpError(passwordsDoesNotMatch);
            throw new Error(passwordsDoesNotMatch);
          }),
        }),
      });
      const data = await request.json();
      if (request.status === 400) {
        // setSignUpError(true);
        console.log(signUpError);
        console.log(Error);
        console.log(data.error);
      }
      if (data && data.token) {
        activateAuth(data.token);
        Router.push('/');
      }
    } catch (error) {
      console.log(error);
      setSignUpError(error);
      console.log(signUpError);
    }
  };

  const validatePasswo = () => {
    if (password === passwordConfirmation) {
      return password.value;
    }
    setPasswordConfirmationError(true);
    throw new Error('The password and the password confirmation must be equals');
  };
  return (
    <>
      <Layout>
        <div>
          <Form title="Log In!" error={signUpError}>
            <Input
              type="text"
              name="username"
              placeholder="username"
              {...username}
            />
            <Input
              type="password"
              name="password"
              placeholder="password"
              {...password}
            />
            <Input
              type="password"
              name="password_confirmation"
              placeholder="password confirmation"
              {...passwordConfirmation}
            />
            <Button type="submit" onClick={handleSignUpSubmit}>
              Log In!
            </Button>
            <span>Do you have an account yet?&nbsp;
              <Link href="/signup">
                <a>Log in here!</a>
              </Link>
            </span>
          </Form>
        </div>
      </Layout>
      <style jsx global>
        {`
          .page_container {
            display: flex;
            align-items: center;
            justify-content: center;
          }
        `}
      </style>
    </>
  );
};

export default signup;
