import 'isomorphic-fetch';
import useRedirectToLogin from '../hooks/useRedirectToLogin';
import Layout from '../components/Layout';
import Button from '../components/Button';
import CardsContainer from '../components/CardsContainer';


const index = ({ allToDoList, toDoListWithQuery, done }) => {
  useRedirectToLogin();
  return (
    <>
      <Layout title="To Do Page">
        <Button />
        {allToDoList
          ? (
            <>
              <CardsContainer
                title="To Do"
                toDoList={allToDoList.filter((toDo) => toDo.done === false)}
              />
              <CardsContainer
                title="Done"
                toDoList={allToDoList.filter((toDo) => toDo.done === true)}
              />
            </>
          )
          : toDoListWithQuery && !!done
            ? <CardsContainer title="Done" toDoList={toDoListWithQuery} />
            : <CardsContainer title="To Do" toDoList={toDoListWithQuery} />}
      </Layout>
      <style jsx global>
        {`
          * {
            margin: 0;
            padding: 0;
          }
        `}
      </style>
    </>
  );
};

export async function getServerSideProps({ query }) {
  try {
    let url = 'http://localhost:8000/api/v1/todo/';

    if (query.done) {
      url = `${url}?done=${query.done}`;
      const response = await fetch(url, {
        headers: {
          Authorization: 'Token 7664ec2dd5c8c914e44449d18ed46b9abb35891c',
        },
      });
      const toDoResponse = await response.json();
      const toDoListWithQuery = toDoResponse.results;
      return { props: { toDoListWithQuery, done: query.done } };
    }

    const response = await fetch(url, {
      headers: {
        Authorization: 'Token 7664ec2dd5c8c914e44449d18ed46b9abb35891c',
      },
    });
    const toDoResponse = await response.json();
    const allToDoList = toDoResponse.results;
    return {
      props: { allToDoList },
    };
  } catch (error) {
    return error;
  }
}

export default index;
