import React from 'react';
import Form from './Form';
import Input from './Input';
import useInputValue from '../hooks/useInputValue';
import TextArea from './TextArea';

const CreateToDo = ({ buttonOnChange }) => {
  const title = useInputValue('');
  const description = useInputValue('');
  return (
    <>
      <div className="container">
        <button type="button" onChange={buttonOnChange}>
          x
        </button>
        <Form title="New To Do!">
          <Input placeholder="title" name="title" {...title} />
          <TextArea placeholder="description" name="description" {...description} />
        </Form>
      </div>
      <style jsx>
        {`
          .container {
            background: #000;
            position: absolute;
            left: 0;
            right: 0;
            top: 0;
            bottom: 0;
            z-index: 999999;
            display: flex;
            align-items: center;
            justify-content: center;
          }
          button {
            position: absolute;
            top: 15px;
            right: 15px;
            outline: none;

          }
        `}
      </style>
    </>
  );
};

export default CreateToDo;
