import { useState, useContext } from 'react';
import Router from 'next/router';
import { userContext } from '../userContext';
import Layout from '../components/Layout';
import useInputValue from '../hooks/useInputValue';
import Input from '../components/Input';
import Form from '../components/Form';
import Button from '../components/Button';

const login = () => {
  // const [username, setUsername] = useState('');
  // const [password, setPassword] = useState('');
  const [loginError, setLoginError] = useState('');
  const { activateAuth } = useContext(userContext);
  const username = useInputValue('');
  const password = useInputValue('');

  const handleSubmit = async (e) => {
    setLoginError('');
    e.preventDefault();
    try {
      const request = await fetch('https://todo-nrestrepo05-api.herokuapp.com/api-token-auth/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: username.value,
          password: password.value,
        }),
      });
      const data = await request.json();
      if (request.status === 400) {
        setLoginError(true);
      }
      if (data && data.token) {
        activateAuth(data.token);
        Router.push('/');
      }
    } catch (error) {
      setLoginError(error);
    }
  };

  return (
    <>
      <Layout>
        <div>
          <Form error={loginError}>
            <Input
              type="text"
              name="username"
              placeholder="username"
              {...username}
            />
            <Input
              type="password"
              name="password"
              placeholder="password"
              {...password}
            />
            <Button type="submit" onClick={handleSubmit}>
              Log In!
            </Button>
          </Form>
        </div>
        {/* <form action="" method="post">
        { loginError ? <div className="Error">Usuario o contrase√±a incorrectos</div> : '' }
        <input
          type="password"
          name="password"
          placeholder="password"
          {...password}
          // value={password}
          // onChange={(e) => setPassword(e.target.value)}
        /> */}
        {/* </form> */}
      </Layout>
      <style global>
        {`
          :global(.page-container) {
            display: flex;
            align-items: center;
            justify-content: center;
          }
        `}
      </style>
    </>
  );
};

export default login;
