import { useRouter } from 'next/router';
import ToDoCard from './ToDoCard';

const CardsContainer = ({ title, toDoList = [] }) => {
  const router = useRouter();

  const handleDelete = async (id) => {
    e.preventDefault();
    try {
      const request = await fetch(`http://localhost:8000/api/v1/todo/${id}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          Authorization: 'Token 7664ec2dd5c8c914e44449d18ed46b9abb35891c',
        },
      });
      const data = await request.json();
      if (request.status === 400) {
      }
      if (request.status === 204) {
        router.reload();
      }
    } catch (error) {
      return error;
    }
  };

  const handleCheck = async (id, done) => {
    e.preventDefault();
    try {
      const request = await fetch(`http://localhost:8000/api/v1/todo/${id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          Authorization: 'Token 7664ec2dd5c8c914e44449d18ed46b9abb35891c',
        },
        body: JSON.stringify({
          done,
        }),
      });
      const data = await request.json();
      if (request.status === 400) {
      }
      if (request.status === 200) {
        router.reload();
      }
    } catch (error) {
      return error;
    }
  };

  return (
    <>
      <div className="container">
        <h2>
          {title}
        </h2>
        <div className="grid-container">
          {toDoList.map((toDo) => (
            <ToDoCard
              key={toDo.id}
              title={toDo.title}
              description={toDo.description}
              done={toDo.done}
              handleCheck={handleCheck(toDo.id, !toDo.done)}
              handleDelete={handleDelete(toDo.id)}
            />
          ))}
        </div>
      </div>
      <style jsx>
        {`
          .container {
            margin: 10px 50px 0 50px;
          }
          .grid-container {
            display: grid;
            grid-template-columns: 1fr;
            gap: 15px;
          }
          h2 {
            color: #64D7EB;
            margin-bottom: 10px;
          }

          @media screen and (min-width: 563px) {
            .grid-container {
              grid-template-columns: 1fr 1fr;
            }
          }
          @media screen and (min-width: 768px) {
            .grid-container {
              grid-template-columns: 1fr 1fr 1fr;
            }
          }
          @media screen and (min-width: 1024px) {
            .grid-container {
              grid-template-columns: 1fr 1fr 1fr 1fr; 
            }
          }
        `}
      </style>
    </>
  );
};

export default CardsContainer;
