import React from 'react';
import useInputValue from '../hooks/useInputValue';

const Input = ({
  type = 'text', name, placeholder, value, onChange,
}) => {
  return (
    <>
      <input
        type={type}
        name={name}
        placeholder={placeholder}
        value={value}
        onChange={onChange}
      />
      <style jsx>
        {`
          input {
            width: 250px;

          }
        `}
      </style>
    </>
  );
};

export default Input;
